        public static int CalculateChecksum(string input)
        {
            var ids = input.Split(' ');

            var doubleLetters = ids.Count(HasDoubleLetter);
            var tripleLetters = ids.Count(HasTripleLetter);

            return doubleLetters * tripleLetters;
        }

        public static bool HasDoubleLetter(string id) =>
            HasMultipleLetters(id, 2);

        public static bool HasTripleLetter(string id) =>
            HasMultipleLetters(id, 3);

        private static bool HasMultipleLetters(string id, int count) =>
            id.Distinct()
              .ToDictionary(c => c,
                            c => id.Count(x => x.Equals(c)))
              .Values
              .Contains(count);
    }
